INSERT INTO vinyl (shs_id, title, author_id, price, genre_id, gender_id, company, release_date, format, desc_text, playlist)VALUES (105078,'Raise the Roof', '192', 1380, 2, 10, 'Warner Music', '2021-11-19', '2LP', '', 'LP1.\n\n1. Quattro (World Drifts In)\n\n2. The Price of Love\n\n3. Go Your Way\n\n4. Trouble With My Lover\n\n5. Searching For My Love\n\n6. Can''t Let Go\n\n \n\nLP2.\n\n1. It Don''t Bother Me\n\n2. You Led Me To the Wrong\n\n3. Last Kind Words Blues\n\n4. High and Lonesome\n\n5. Going Where the Lonely Go\n\n6. Somebody Was Watching Over Me');
